def timeParser(elapsedTime):
    hour = int((elapsedTime / 60) / 60)
    minute = int((elapsedTime / 60) % 60)
    second = int(elapsedTime % 60)

    if second < 10:
        second = "0" + str(second)
    if minute < 10:
        minute = "0" + str(minute)
    if hour < 10:
        hour = "0" + str(hour)

    newtime = "{}:{}:{}".format(hour, minute, second)

    return newtime


